import React, { useState } from "react";
import Todo from "./components/Todo";
import FilterButton from "./components/FilterButton";
import Form from "./components/Form";
import { nanoid } from "nanoid";


function App(props) {
  const [tasks, setTasks] = useState(props.tasks);


  function toggleTaskCompleted(id) {

    const updatedTasks = tasks.map(task => {
      //if the task has matching id, 
      if (id === task.id) {
        //use object spread to make a new object 
        // whoos completed prop has been inverted and
        return {
          ...task, completed: !task.completed
        }
      }
      return task;
    })
    setTasks(updatedTasks)
    console.log(tasks[1])
  }


  const taskList = tasks.map(task => <Todo id={task.id} name={task.name} completed={task.completed} key={task.id} toggleTaskCompleted={toggleTaskCompleted} />)
  const tasksNoun = taskList.length !== 1 ? 'tasks' : 'task';
  const headingText = `${taskList.length} ${tasksNoun} remaining`;

  function addTask(name) {

    if (name.length === 0) {
      return
    }

    const newTask = {
      id: 'todo-' + nanoid(), name: name, completed: true
    };
    setTasks([...tasks, newTask]);
  }







  return (
    <div className="todoapp stack-large">
      <h1>{headingText}</h1>
      <Form addTask={addTask} />
      <ul
        role="list"
        className="todo-list stack-large stack-exception"
        aria-labelledby="list-heading"
      >
        {taskList}

      </ul>
    </div>
  );
}
export default App;
