import React, { useState } from 'react';
import { nanoid } from "nanoid";

function Form(props) {

    const [name, setName] = useState('')

    function handleChange(e) {
        setName(e.target.value)
    }

    function nanTest() {
        let a = nanoid()
        alert(a)
    }

    function handleSubmit(e) {
        e.preventDefault();
        props.addTask(name);
        setName("");
    }

    function randomNumber() {
        let a = Math.ceil(Math.random() * 1000);
        alert(a)
    }

    return (
        <form onSubmit={handleSubmit} required>
            <h2 className="label-wrapper">  

                <label htmlFor="new-todo-input" className="label__lg">What needs to be done?</label>
            </h2>

            <input

                type="text"
                id={Math.ceil(Math.random() * 1000)}
                className="input input__lg"
                name="text"
                autoComplete="off"
                value={name}
                onChange={handleChange}

            />
            <div class="button__wrap"><button class="button" style="--hue: 107.39120288824535"
            type="submit">ADD</button>
    <div class="button__shadow"></div>
</div>
            <button  className="btn btn__primary btn__lg">
                Add
            </button>


        </form>
    )
}

export default Form;
